function CarregarDadosGastosTotais(){$.ajax({url:"Despesas/GastosTotais",method:"POST",success:function(n){new Chart(document.getElementById("GraficoGastosTotais"),{type:"line",data:{labels:PegarMeses(n),datasets:[{label:"Total gasto",data:PegarValores(n),backgroundColor:"#ecf0f1",borderColor:"#2980b9",fill:!1,spanGaps:!1}]},options:{title:{display:!0,text:"Total gasto"}}})}})}