function CarregarDadosGastosTotaisMes(){$.ajax({url:"Despesas/GastosTotaisMes",method:"POST",data:{mesId:1},success:function(n){$("canvas#GraficoGastoTotalMes").remove();$("div.GraficoGastoTotalMes").append('<canvas id="GraficoGastoTotalMes" style="height:400px;width:400px;"><\/canvas>');var t=document.getElementById("GraficoGastoTotalMes").getContext("2d"),i=new Chart(t,{type:"doughnut",data:{labels:["Restante","Total gasto"],datasets:[{label:"Total gasto",backgroundColor:["#27ae60","#c0392b"],data:[n.salario-n.valorTotalGasto,n.valorTotalGasto]}]},options:{responsive:!1,title:{display:!0,text:"Total gasto no Mês"}}})}})}$(".escolhaMes").on("change",function(){var n=$(".escolhaMes").val();$.ajax({url:"Despesas/GastosTotaisMes",method:"POST",data:{mesId:n},success:function(n){$("canvas#GraficoGastoTotalMes").remove();$("div.GraficoGastoTotalMes").append('<canvas id="GraficoGastoTotalMes" style="height:400px;width:400px;"><\/canvas>');var t=document.getElementById("GraficoGastoTotalMes").getContext("2d"),i=new Chart(t,{type:"doughnut",data:{labels:["Restante","Total gasto"],datasets:[{label:"Total gasto",backgroundColor:["#27ae60","#c0392b"],data:[n.salario-n.valorTotalGasto,n.valorTotalGasto]}]},options:{responsive:!1,title:{display:!0,text:"Total gasto no Mês"}}})}})});